import React from 'react';

class ProjectCard extends React.Component {
  constructor() {
    super();

    this.handleMouse = this.handleMouse.bind(this);
  }

  handleMouse({ type, currentTarget }) {
    const {id, classList} = currentTarget;
    let operation = 'remove';
    if (type === 'mouseenter') id = 'projetoSelecionado';
  }

  render() {
    const { name, img, imgName, link} = this.props;

    return (
      <div className="projeto" onMouseEnter={ this.handleMouse }>
        <div className="janelaNavegador">
          <div className="botoes">
            <div className="botao"></div>
            <div className="botao"></div>
            <div className="botao"></div>
          </div>
          <a href={ link } target="_blank">
            <img src={ img } alt={ imgName } />
          </a>
        </div>
        <p>{name}</p>
      </div>
    );
  }
}

export default ProjectCard;
