import React from 'react';
import { Link } from 'react-router-dom';
import { discordIcon, gitHubIcon, linkedInIcon } from '../services/icons';

class Footer extends React.Component {
  constructor() {
    super();

    this.handleMouseEnter = this.handleMouseEnter.bind(this);
    this.handleMouseLeave = this.handleMouseLeave.bind(this);
  }

  // handleMouseEnter({ event, currentTarget }) {
  //   const {id, classList} = currentTarget;
  //   console.log(event);
  //   classList.add(`${id}Selecionado`)
  // }

  handleMouseEnter(evento) {
    console.log(evento);
  }

  handleMouseLeave({ currentTarget }) {
    const {id, classList} = currentTarget;
    classList.remove(`${id}Selecionado`)
  }

  render() {
    return (
      <footer id="footer">
        <div>
          <a id="linkedIn" href="https://www.linkedin.com/in/matheuscontassot/" target="_blank" onMouseEnter={ this.handleMouseEnter } onMouseLeave={ this.handleMouseLeave }>
            {linkedInIcon()}
          </a>
          <a id="gitHub" href="https://github.com/MattContassot" target="_blank" onMouseEnter={ this.handleMouseEnter } onMouseLeave={ this.handleMouseLeave }>
            {gitHubIcon()}
          </a>
          <Link to="/404">
            {discordIcon()}
          </Link>        
        </div>
      </footer>
    );
  }
}

export default Footer;
